// Map French month names to English
const monthMap = {
  janvier: 'January',
  février: 'February',
  mars: 'March',
  avril: 'April',
  mai: 'May',
  juin: 'June',
  juillet: 'July',
  août: 'August',
  septembre: 'September',
  octobre: 'October',
  novembre: 'November',
  décembre: 'December'
};

// Parse date function
const parseDate = (dateString) => {
  const [day, month, year] = dateString.split(' ');
  const englishMonth = monthMap[month];
  return new Date(`${englishMonth} ${day}, ${year}`);
};

// Sort events by date function
const sortEventsByDate = (events) => {
  return events.sort((a, b) => parseDate(a.date) - parseDate(b.date));
};
