const Events = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await fetch('/api/post-blogs?populate=*');
        const data = await response.json();

        const apiEvents = data.data.map((item) => {
          const { Title, Description, content, Mediafiles, subcategory } = item.attributes;

          // Extract date and location from Description
          let date = 'Unknown Date';
          let location = 'Unknown Location';
          if (Description) {
            Description.forEach(paragraph => {
              const text = paragraph?.children?.map(child => child.text).join('');
              if (text) {
                const dateMatch = text.match(/<date>(.*?)<\/date>/);
                const locationMatch = text.match(/<location>(.*?)<\/location>/);
                
                if (dateMatch) date = dateMatch[1];
                if (locationMatch) location = locationMatch[1];
              }
            });
          }

          // Extract image URL
          const imageUrl = Mediafiles?.data?.[0]?.attributes?.formats?.large?.url
            ? `${Mediafiles.data[0].attributes.formats.large.url}`
            : '';

          return {
            id: item.id,
            title: Title || 'No Title',
            date,
            location,
            content: content || 'No Content',
            imageUrl,
            subcategory: subcategory?.data?.attributes?.name || 'Unknown'
          };
        }).filter((item) => item.subcategory === "Événements");

        // Combine static and API events and sort them by date
        const allEvents = [...staticEvents, ...apiEvents];
        setEvents(sortEventsByDate(allEvents));
      } catch (error) {
        console.error("Error fetching events data from API:", error);
      }
    };

    fetchEvents();
  }, []);

  const imageLoaded = () => {
    // Your image loaded logic here
  };

  return (
    <>
      <Row>
        <img className="backnavhead" src={backnavhead} aria-hidden="true" alt="Background" />
      </Row>
      <Container className="py-5">
        <h1 className="events-title">Nos Événements</h1>
        <p>
          Explorez les activités et les rassemblements axés sur l'inclusion et l'accessibilité pour les personnes handicapées en Tunisie. Consultez les conférences, ateliers, séminaires et événements communautaires à venir. Engagez-vous dans des conversations pertinentes, rencontrez des spécialistes et participez à des projets locaux.
        </p>
        <Row className="row-cols-1 row-cols-md-3 g-4">
          {events.map(event => (
            <Col key={event.id}>
              <Link to={`/actualites-et-evenements/evenements/${event.title}`} className="text-decoration-none">
                <div className="custom-card h-100" style={{ position: 'relative' }}>
                  <div className="custom-bottom-right">{new Date(event.date).toLocaleDateString('fr-FR', { day: '2-digit', month: 'short', year: 'numeric' })}</div>
                  <div className="custom-top-right">
                    <div className="custom-tags-list mb-2">
                      <span className="custom-tags-list-town text-uppercase">{event.location.split(',')[0]}</span>
                    </div>
                  </div>
                  <div className="custom-image-container">
                    <div className="custom-bottom-left">
                      <div className="custom-thumbnail" style={{ backgroundColor: '#ffa5009e' }}>
                        <div className="custom-thumbnail-date">
                          <span className="custom-thumbnail-date-day">{new Date(event.date).getDate()}</span>
                          <span className="custom-thumbnail-date-month">{new Date(event.date).toLocaleString('default', { month: 'short' })}</span>
                        </div>
                      </div>
                    </div>
                    <img 
                      src={event.imageUrl} 
                      onLoad={imageLoaded} 
                      className="custom-card-img-top" 
                      alt={event.title} 
                    />
                    <div className="custom-image-overlay custom-text-over text-center d-flex justify-content-center align-items-center text-uppercase">
                      <div>
                        {event.title}
                      </div>
                    </div>
                  </div>
                </div>
              </Link>
            </Col>
          ))}
        </Row>
      </Container>
    </>
  );
};

export default Events;